sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '' || newValue == oldValue) {
      return;
   }

	//Instantiate the GetEmailAddress script include using GlideAjax method
	var getEmailAddr = new GlideAjax('GetEmailAddress');
	//call addparam method and pass the function which is present in Script include  
	getEmailAddr.addParam('sysparam_name','getEmail');
	//call addparam method and pass the sysid
	getEmailAddr.addParam('sysparam_userID', g_form.getValue('u_requested_for'));
	//send the request to server - call getXML method and pass callback function
	getEmailAddr.getXML(populateEmailField);

	//Define callback function whose params will be the response from server
	function populateEmailField(response){
		var emailFrmScriptInclude = response.responseXML.documentElement.getAttribute("answer");
		g_form.clearValue('u_requested_for_email');
		g_form.setValue('u_requested_for_email', emailFrmScriptInclude);
	}
   //Type appropriate comment here, and begin script below

}
