sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
          /* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
          /* Optional GlideRecord */ event) {

          // Add your code here
	var rightnowTime = new GlideDateTime();

	var overdueNeeditTasks = new GlideRecord('x_58872_needit_needit_task');
	overdueNeeditTasks.addQuery('due_date', '<=', rightnowTime);
	overdueNeeditTasks.addQuery('status', '<', 3);
	overdueNeeditTasks.addQuery('assigned_to', current.assigned_to);
	overdueNeeditTasks.query();

	template.print("In addition to this overdue NeedIt Task, you have " + (overdueNeeditTasks.getRowCount() - 1) + " more overdue NeedIt Tasks.");


})(current, template, email, email_action, event);
