sysauto_script.script:
//Retrieve the value of the Application Property
var howLongOverdue = gs.getProperty('x_58872_needit.autoCloseOverdue');

var overdueNeeditTasks = new GlideRecord('x_58872_needit_needit_task');
overdueNeeditTasks.addQuery('due_date','<=', gs.daysAgo(howLongOverdue));
overdueNeeditTasks.addQuery('state','<',3);
overdueNeeditTasks.query();

while (overdueNeeditTasks.nex())
	{
		gs.info("Auto Close Needit Task No: " + overdueNeeditTasks.number);
		overdueNeeditTasks.work_notes = "Record closed because it was too far past the Due date.";
		overdueNeeditTasks.state = 4;
		overdueNeeditTasks.update();
	}
